$("document").ready(function(){var e="http://www.freecodecamp.com/news/hot";$.getJSON(e,function(e){for(var i=JSON.stringify(e),n=0;n<e.length;n++){var s=JSON.stringify(e[n].link),t=JSON.stringify(e[n].headline),a=JSON.stringify(e[n].image),r=JSON.stringify(e[n].upVotes.length),g=JSON.stringify(e[n].author.picture),c=JSON.stringify(e[n].author.username);a=e[n].hasOwnProperty("image")&&e[n].image?JSON.stringify(e[n].image):g,$("#content").append('<div class="col-xs-4">\n         <a href="'+s.replace(/\"/g,"")+'" target="_blank">\n         <img src='+a+' class=" img-responsive news-image" >\n         <div class="dim-layer"></div>\n         <h5 class="news-title">'+t.replace(/\"/g,"")+'</h5></a>\n         <p class="upvotes">'+r+"</p>\n         </div>")}})});