$("document").ready(function(){var e="http://www.freecodecamp.com/news/hot";$.getJSON(e,function(e){for(var i=JSON.stringify(e),a=0;a<e.length;a++){var n=JSON.stringify(e[a].link),s=JSON.stringify(e[a].headline),t=JSON.stringify(e[a].image),r=JSON.stringify(e[a].upVotes.length),g=JSON.stringify(e[a].author.picture),c=JSON.stringify(e[a].author.username);t=e[a].hasOwnProperty("image")&&e[a].image?JSON.stringify(e[a].image):g,$("#content").append('<div class="col-md-4 col-sm-6 col-xs-12">\n         <a href="'+n.replace(/\"/g,"")+'" target="_blank">\n         <img src='+t+' class=" img-responsive news-image" >\n         <div class="dim-layer"></div>\n         <h5 class="news-title">'+s.replace(/\"/g,"")+'</h5></a>\n         <p class="upvotes"><i class="fa fa-heart heart"></i> '+r+"</p>\n         </div>")}})});