$("document").ready(function(){var e="https://www.freecodecamp.com/news/hot";$.getJSON(e,function(e){for(var a=JSON.stringify(e),t=0;t<e.length;t++){var r=JSON.stringify(e[t].link),i=JSON.stringify(e[t].headline),n=JSON.stringify(e[t].image),s=JSON.stringify(e[t].upVotes.length),o=JSON.stringify(e[t].author.picture),c=JSON.stringify(e[t].author.username);n=e[t].hasOwnProperty("image")&&e[t].image?JSON.stringify(e[t].image):o,$("#content").append('<div class="col-md-4 col-sm-6 col-xs-12">\n         <a href="'+r.replace(/\"/g,"")+'" target="_blank">\n         <img src='+n+' class=" img-responsive news-image" >\n         <div class="dim-layer"></div>\n         <h5 class="news-title">'+i.replace(/\"/g,"")+'</h5>\n         <p class="upvotes"><i class="fa fa-heart heart"></i> '+s+"</p>\n         </a>\n         </div>")}$("#content").append("<div class='col-xs-12 footer'><p>Created by <a href='https://twitter.com/amrsekilly'>Amr Sekilly</a>. As a front-end project for <a href='http://www.freecodecamp.com/amrme'>Free Code Camp's Front-end degree </a>(2016).</p></div>")})});
