$("document").ready(function(){var e="https://www.freecodecamp.com/news/hot";$.getJSON(e,function(e){for(var i=JSON.stringify(e),n=0;n<e.length;n++){var a=JSON.stringify(e[n].link),s=JSON.stringify(e[n].headline),t=JSON.stringify(e[n].image),r=JSON.stringify(e[n].upVotes.length),g=JSON.stringify(e[n].author.picture),c=JSON.stringify(e[n].author.username);t=e[n].hasOwnProperty("image")&&e[n].image?JSON.stringify(e[n].image):g,$("#content").append('<div class="col-md-4 col-sm-6 col-xs-12">\n         <a href="'+a.replace(/\"/g,"")+'" target="_blank">\n         <img src='+t+' class=" img-responsive news-image" >\n         <div class="dim-layer"></div>\n         <h5 class="news-title">'+s.replace(/\"/g,"")+'</h5>\n         <p class="upvotes"><i class="fa fa-heart heart"></i> '+r+"</p>\n         </a>\n         </div>")}})});
